adjust_extent_cmip6_hist_ssp585<-function(
  
){
  
  files = list.files('/media/root/Shen_drive1/CMIP6_combined',full.names = T)
  
  for(i in c(1,2,4)){
    f1 = files[i]
    f1 = list.files(f1,full.names = T)
    print(i)
    for(j in 2){
      f2 = list.files(f1[j],full.names = T)
      
      for(z in 1:length(f2)){
        library(raster)
        library(ncdf4)
        tmp = stack(f2[z])
        extent(tmp) = extent(0,360,0,90)
        
        e1 = extent(0,180,0,90)
        e2 = extent(180,360,0,90)
        
        tmp1 = crop(tmp,e1)
        tmp2 = crop(tmp,e2)
        
        extent(tmp2) = extent(-180,0,0,90)
        
        tmp = merge(tmp2,tmp1)
        writeRaster(tmp,f2[z],format = 'CDF',overwrite = T)
      }
      
    }
    
    
    
  }
  
  
  
  
}
adjust_extent_cmip6_sst<-function(
  
){
  
  files = list.files('/media/root/Shen_drive1/CMIP6_combined',
                     full.names = T)
  
  for(i in 3){
    f1 = files[i]
    f1 = list.files(f1,full.names = T)
    print(i)
    for(j in 1:2){
      f2 = list.files(f1[j],full.names = T)
      
      for(z in 1:length(f2)){
        library(raster)
        library(ncdf4)
        tmp = try(stack(f2[z]),
                  silent = T)
        
        if('try-error' %in% class(tmp)){
          tmp = fread(f2[z])
          tmp = as.data.frame(tmp)
          
          loc = tmp[,1:2]
          
          minlong = min(loc[,1])
          minlat = min(loc[,2])
          maxlong = max(loc[,1])
          maxlat = max(loc[,2])
          
          diflong = minlong -()
          
          coordinates(tmp1) = ~long+lat
          gridded(tmp1) = T
          
          tmp1 = raster(tmp1)
          ggplot()+
            geom_point(data = tmp1,
                      aes(x = long,
                          y = lat,
                          color = value),
                      shape = 2)
            
        }
        extent(tmp) = extent(0,360,0,90)
        
        e1 = extent(0,180,0,90)
        e2 = extent(180,360,0,90)
        
        tmp1 = crop(tmp,e1)
        tmp2 = crop(tmp,e2)
        
        extent(tmp2) = extent(-180,0,0,90)
        
        tmp = merge(tmp2,tmp1)
        writeRaster(tmp,f2[z],format = 'CDF',overwrite = T)
      }
      
    }
    
    
    
  }
  
  
  
  
}
