    fwrite(rettrain,output_train585[i])
    
    #matplot(cbind(revfits,))
    
    a = tmpb$coefficients[1]
    b = tmpb$coefficients[2]
    c = tmpb$coefficients[3]
    d = tmpb$coefficients[4]
    
    retdf = data.frame(
      a = a,
