    moun_sum = do.call('c',moun_sum)
    full_sum = do.call('c',full_sum)
    
    jishui_sum = full_sum -moun_sum
    
    return(cbind(moun_sum,jishui_sum))
  }
  
  north_df = cal_pr_sum(ear5_eva,north,north_moun)
