    tws_box = cbind(tws_box,tws_subs[,i])
    cor_box = c(cor_box,tmpcor)
    #print(tmpcor)
  }
  tws_box = tws_box[,-1]
  cor_box = cor_box[-1]
  shp_com = do.call('bind',shp_list)
  
  
  
