                 aes(x = x,xend = xend,
                     y = y,yend = y),
                 size = 0.5,
                 color = 'black',
                 arrow = arrow(angle=30,
                               unit('0.25','cm'),
                               type = 'closed'))+
    geom_segment(data = segdfh,
                 aes(x = xend,xend = x,
                     y = y,yend = y),
                 size = 0.5,
                 color = 'black',
                 arrow = arrow(angle=30,
                               unit('0.25','cm'),
                               type = 'closed'))+
    geom_segment(data = segdfv,
                 aes(x = x,xend = x,
                     y = y,yend = yend),
                 size = 0.5,
                 color = 'black')+
    
    theme_bw()+
    scale_x_continuous(breaks = c(2018,2031,2050))+
    facet_wrap(~type,nrow = 1)+
    text_theme+
    guides(color = guide_legend(title = ''),
           linetype = guide_legend(title = ''))+
