    tmp = cor.test(tmp1,tmp2)$p.value
    return(tmp)
  }
  cor_fun <- function(i){
    tmp1 = pos_tws[,i]
    tmp2 = pos_fresh[,i]
    tmp = cor.test(tmp1,tmp2)$estimate
    #tmp = max(ccf(tmp1,tmp2)$acf)
    return(tmp)
  }
  
  i = 1:ncol(pos_tws)
  pv = do.call('c',lapply(i,cor_fun_p))
