                  '(0.5,1]' = 2)
  
  color_scale1 = c('(0,0.01]' = color2[2],
                  '(0.01,0.05]'= color2[2],
                  '(0.05,0.1]'= color2[1],
                  '(0.1,0.25]' = color2[1],
                  '(0.25,0.5]' = color2[1],
                  '(0.5,1]' = color2[1])
  
  world = shp_management('world')
