        lat = pos_country$lat[tmp],
        label = letters[i+6]
      )
      return(retdf)
    }
  }
  neg_label = do.call(rbind,lapply(i,match_neg_country))
  pos_label = do.call(rbind,lapply(i,match_pos_country))
  
  map_label1 = data.frame(
    x = 100,
    y = 85,
