}
fig2_backup_plot_tws_mmk<-function(
  
){
  library(ggnewscale)
  library(ggplot2)
  library(raster)
  
  # import world shp
  source('/home/share/R_project/xinjiang_vapor/shp_management.R')
  world = shp_management('world')
