    x = ts(x,start = c(2003,1),
           frequency = 12)
    xt = decompose(x)$trend
    naid = which(is.na(xt))
    xt = xt[-naid]
    
    x = xt
    
    x = (x -mean(x,na.rm = T))/(max(x,na.rm = T)-
                                  min(x,na.rm = T))
    return(x)
  }
  
  #era5_pme = stand_fun(era5_pme)
  #tws = stand_fun(tws)
  
