                          levels = paste0('SW',1:10))
  
  pline1 = ggplot()+
    #geom_ribbon(data = df2,aes(x = date,
    #                           ymax = max,
    #                           ymin = min),
    #            fill = '#87CEFA',
    #            alpha = 0.5)+
    geom_text(data = cr245,
              aes(x = x, y = y,label = label),
