        fill= 'transparent',
        color = 'transparent'
      )
      
    )
  pbar2 = pbar2+
    theme(
      axis.title = element_blank(),
      axis.text.y = element_text(
        size = 9,color = 'black'
      ),
      axis.text.x = element_blank(),
      axis.line.x = element_blank(),
      axis.ticks.x = element_blank(),
      plot.background  = element_rect(
