        color = 'transparent'
      )
    )
  
  p123 = ggdraw()+
    draw_plot(p12)+
    draw_plot(pbar1,x = 0.06, y = 0.352,
              width = 0.11, height = 0.22)+
    draw_plot(pbar2,x = 0.06, y = 0.02,
              width = 0.11, height = 0.22)+
