    scale_color_manual(values = cols)+
    facet_wrap(~variable,ncol = 2)+
    theme_bw()+
    theme(legend.position = 'none')+
    ylab('Normalized indices')
  
  library(data.table)
  
  
  source('/home/share/R_project/xinjiang_vapor/cal_mean_tws_among_windows.R')
