                                                  mode = 'max')
  mean_tws245$type = '(a) Regional mean standard indices under SSP245'
  mean_tws585$type = '(b) Regional mean standard indices under SSP585'
  
  pmedf1 = pmedf
  colnames(pmedf1) = c('date','variable','value')
  pmedf245 = pmedf1[
    which(pmedf1$variable %in% c('SSP245_PME1',
                                 'SSP245_PME3')),]
  pmedf585 = pmedf1[
