  pme3_mean585 = apply(pme3_pj585,1,mean)
  sst1_pj245 = as.matrix(sst1_ssp245[175:576,])
  sst3_pj245 = as.matrix(sst3_ssp245[175:576,])
  
  source('/home/share/R_project/xinjiang_vapor/trend_fun.R')
