  )
  ##########
  p10 = p10+
      text_theme_set+
    theme(strip.text = element_blank(),
          axis.title.x = element_blank(),
          legend.position = 'none')
  
  source('/home/share/R_project/xinjiang_vapor/import_IncMSE_data_figs1011.R')
  
