             aes(x = SWs,
                 y = value,
                 fill = PME),
             stat = 'identity',
             position = 'dodge',
             color = 'white',
             width = 0.8)+
    geom_bar(data = imse585,
             aes(x = SWs,
                 y = value,
                 fill = PME),
             stat = 'identity',
             position = 'dodge',
             color = 'white',
             width = 0.8)+
    geom_text(data = labeldf,
              aes(x = x,y = y,
                  label = label),
              color = 'black',
              size = 4)+
    geom_text(data = text_df1,
              aes(x = x,y = y+0.2,
                  label = round(y,2)),
              position = position_dodge2(0.8),
              size = 4,
              color = 'black')+
    facet_wrap(~type,nrow = 1)+
    scale_fill_lancet(alpha = 0.8)+
    theme_bw()+
    ylab('IncMSE (%)')
  
  
  p11 = p11+text_theme_set+
    theme(strip.text = element_blank(),
          axis.title.x = element_blank(),
          legend.position = 'none')
  
  p10 = p10+scale_y_continuous(
    breaks = seq(0,8,2),
    labels = paste0(seq(0,8,2),'.0')
  )
  p11 = p11+scale_y_continuous(
    breaks = seq(0,12,2),
    labels = paste0(seq(0,12,2),'.0')
  )
  
  library(cowplot)
  p12 = plot_grid(
    p10,p11,
    align = 'lr',
    axis = 'h',
    rel_widths = c(1,1),
    rel_heights = c(1,1),
    ncol = 1
  )
  
  library(ggpubr)
  p12legend = as_ggplot(get_legend(
