    f1 = list.files(f1,full.names = T)
    print(i)
    for(j in 1:2){
      f2 = list.files(f1[j],full.names = T)
      
      for(z in 1:length(f2)){
        library(raster)
        library(ncdf4)
        tmp = try(stack(f2[z]),
                  silent = T)
        
        if('try-error' %in% class(tmp)){
          tmp = fread(f2[z])
          tmp = as.data.frame(tmp)
          
          loc = tmp[,1:2]
          
          minlong = min(loc[,1])
          minlat = min(loc[,2])
          maxlong = max(loc[,1])
          maxlat = max(loc[,2])
          
          diflong = minlong -()
          
          coordinates(tmp1) = ~long+lat
          gridded(tmp1) = T
          
          tmp1 = raster(tmp1)
          ggplot()+
            geom_point(data = tmp1,
                      aes(x = long,
                          y = lat,
                          color = value),
                      shape = 2)
            
        }
        extent(tmp) = extent(0,360,0,90)
        
        e1 = extent(0,180,0,90)
