        tmp1 = crop(tmp,e1)
        tmp2 = crop(tmp,e2)
        
        extent(tmp2) = extent(-180,0,0,90)
        
        tmp = merge(tmp2,tmp1)
        writeRaster(tmp,f2[z],format = 'CDF',overwrite = T)
