cal_increasing_rate_pmedf <- function(df){
  
  id = c('SSP245_PME1','SSP245_PME3',
         'SSP585_PME1','SSP585_PME3')
  c1 = 1
  c2 = 1
  for(i in 1:4){
    tmpid = which(df$type %in% id[i])
    tmpdf = df[tmpid,3]
    
    c12 = (tmpdf[14]- tmpdf[1])/(abs(tmpdf[1]))
    c23 = (tmpdf[33] - tmpdf[1])/abs(tmpdf[1])
    
    c1 = c(c1,round(c12))
    c2 = c(c2,round(c23))
    
  }
  c1 = c1[-1]
  c2 = c2[-1]
  
  retdf1 = data.frame(
    x = rep(7,10),
    label = paste0(c1[1]),
    type = id
  )
  retdf2 = data.frame(
    x = rep(23,10),
    label = paste0(c2,'%'),
    type = id
  )
}
