cor_filter_fun <-function(
  ret_df
){
  cor2 = 1
  for(i in 1:10 ){
    cor1 = 1
    for(j in 1:8){
      tmpdf = ret_df[[i]][[1]][[j]][[2]]
      tmpcor = cor(tmpdf[,1],tmpdf[,2])
      cor1 = c(cor1,tmpcor)
    }
    cor1 = cor1[-1]
    cor2 = cbind(cor2,cor1)
  }
  cor2 = cor2[,-1]
  return(cor2)
}
