 df
){
  
  g1 = 1:10
  g2 = 11:20
  
  df1 = df[which(df$type %in% g1),]
  df2 = df[which(df$type %in% g2),]
  
  cor1_box = 1
  cor2_box = 1
  for(i in 1:10){
    tmpid1 = which(df1$type ==g1[i])
    tmpid2 = which(df2$type ==g2[i])
    
    tmpdf1 = df1[tmpid1,]
    tmpdf2 = df2[tmpid2,]
    
    cor1 = cor(tmpdf1$TWS,
               tmpdf1$value)
    
    cor2 = cor(tmpdf2$TWS,
               tmpdf2$value)
    
    cor1_box = c(cor1_box,
                 cor1)
    cor2_box = c(cor2_box,
                 cor2)
  }
  cor1_box = cor1_box[-1]
  cor2_box = cor2_box[-1]
  
  cor1_box = data.frame(
    x = 0.05,
    y = -0.20+0.05,
    cor = paste0('Cor: ',round(cor1_box,2)),
    type = 1:10
  )
  
  cor2_box = data.frame(
    x = 0.05,
    y = -0.20+0.05,
    cor = paste0('Cor: ',round(cor2_box,2)),
    type = 11:20
  )
  corbox = rbind(cor1_box,cor2_box)
  pbox = data.frame(
    x = 0.05,
    y = -0.23+0.02,
    cor = 'p<0.05',
    type = 1:20
  )
  
  return(list(corbox,pbox))
}
