    con_names = c('as','eu','naf','na')
    
    idr = which(con_names == mode)
    input_poly = files[idr]
  }
  input_poly <<- input_poly
  
  # input crop 
  input_crop = paste0('cluster_shp/',mode)
  input_crop = list.files(input_crop, pattern = '*shp$',full.names = T)
