      loc = tmpsst[,1:2]
      # import sub regions
      r1e = extent(r1)
      r2e = extent(r2)
      r3e = extent(r3)
      r4e = extent(r4)
      
      id1 = which(loc[,1]>=r1e[1] &
                    loc[,1]<=r1e[2] &
                    loc[,2]>=r1e[3] &
                    loc[,2]<=r1e[4])
      id2 = which(loc[,1]>=r2e[1] &
                    loc[,1]<=r2e[2] &
                    loc[,2]>=r2e[3] &
                    loc[,2]<=r2e[4])
      id3 = which(loc[,1]>=r3e[1] &
                    loc[,1]<=r3e[2] &
                    loc[,2]>=r3e[3] &
                    loc[,2]<=r3e[4])
      id4 = which(loc[,1]>=r4e[1] &
                    loc[,1]<=r4e[2] &
                    loc[,2]>=r4e[3] &
