mask_points_solo_shp<-function(
  df_in,shp
){
  # mask points df with solo thread
  # demands input of df and shp
  library(raster)
  library(maps)
  library(maptools)
  library(data.table)
  dfsce = df_in
  coordinates(dfsce) = ~ long+lat
