      obs_box = cbind(obs_box,tmpssp245[,1])
    }    
    ssp245_box = ssp245_box[,-1]
    colnames(ssp245_box)= model_names
    obs_box = obs_box[,-1]
    obs_box = obs_box[,1]
    
    valid_ssp245 = data.frame(
      tws = obs_box,
      ssp245_box
    )
    
