    OCT = full_df_li[[10]][,3],
    NOV = full_df_li[[11]][,3],
    DEC = full_df_li[[12]][,3]
  )
  
  idnan = which(is.na(full_df$JAN))
  full_df = full_df[-idnan,]
  fulldfm = reshape2::melt(full_df,
                           c('long','lat'))
  
  lessid= which(fulldfm$value<0)
