  cid2 = seq(1,nrow(cur_df2),200)
  
  cur_df1 = cur_df1[cid1,]
  cur_df2 = cur_df2[cid2,]
  
  
  cols_cur = c('#E6E6FA','#FFE4E1')
  
  p1 = ggplot()+
