nc_to_df <- function(
  a
){
  
  
  long = try(ncvar_get(a,'longitude'),
             silent = T)
  lat = try(ncvar_get(a,'latitude'),
            silent = T)
  
  if('try-error' %in% class(long)){
    long = ncvar_get(a,'lon')
  }
  
  if('try-error' %in% class(lat)){
    lat = ncvar_get(a,'lat')
  }
  
  nlong = dim(long)
  nlat = dim(lat)
  
  tos = ncvar_get(a,'tos')
  
  n = dim(tos)[3]
  
  long = as.vector(long)
  lat = as.vector(lat)
  
  long <<- long
  lat <<- lat
  tos <<- tos
  i <<- 1:n
  subs_fun <- function(i){
    library(raster)
    tmptos = tos[,,i]
    tmptos = as.vector(tmptos)
    return(tmptos)
  }
  
  ret <- lapply(i,subs_fun)
  ret = do.call('cbind',ret)
  
  df = data.frame(
    long = long,
    lat = lat,
    ret
  )
  
  return(df)
  
}
