               frequency = 12)
        x = decompose(x)$trend
        
        xt = as.numeric(x)
        stand_fun <-function(y){
          y = (y - mean(y,na.rm = T))/(max(y,na.rm = T) -min(y,na.rm = T))
        }
        xy  = stand_fun(xt)
        return(xy)
      }
      xm = apply(x,2,sub_fun)
      return(xm)
    }
    # sp245 data
    ###############
    pme1_tv245 = pme1_ssp245[1:174,]
    pme3_tv245 = pme3_ssp245[1:174,]
    sst1_tv245 = sst1_ssp245[1:174,]
