  mod_par_cart = 1
  for(ntree in seq(100,1000,100)){
    set.seed(432)
    rf_mtry = train(snowm ~ .,
                    data = train,
                    method = 'rf',
                    ntree = ntree,
                    tuneGrid = tuneGrid,
                    trControl = trControl,
                    importance = T)
    
    rf_mod = rf_mtry$results[,c(1,3)]
    rf_mod = cbind(rf_mod,ntree)
    mod_par_cart = rbind(mod_par_cart,rf_mod)
