    shps = shapefile(path)
    return(shps)
  }else if(pattern == 'china_city'){
    #setwd('/media/sdb5/Vapor_projcts/Vapor_tibet')
    path = '/media/sdb1/shp/China/cities.shp'
    shps = shapefile(path)
    return(shps)
  }
  
  
  
  
  
  
  
}


  
  
