                              size = fontsize),
    legend.text = element_text(
      color = 'black',
      size = fontsize),
    legend.title = element_text(
      color = 'black',
      size = fontsize),
    legend.position = 'bottom',
    strip.text = element_text(
      color = 'black',
      size = fontsize)
    ##############
  )
  p1 = ggplot()+
    geom_tile(data = tws_meanm_agg,
              aes(x = Longitude,
                  y = Time,
