    theme_bw()+
    text_theme_set
  
  # input of currents ocean
  cur_df = as.data.frame(
    fread('analysis_output/fig4/current_df.csv')
  )
  
  idless50 = which(cur_df$lat <= 50)
  cur_df = cur_df[idless50,]
  
  idbig1 = which(cur_df$u>0 &
                   cur_df$v >0)
  idbig2 = which(cur_df$u<0 &
