speed_up_krige<-function(
  df,
  locdf
){
  library(maps)
  library(fields)
  library(ggplot2)
  library(geoR)
  library(maptools)
  library(png)
  700
  df = loc1
  
  locdf = df[,1:2]
  
  subs_long = seq(-180,180,180)
  subs_lat = seq(-60,90,75)
  
  subslong1 = rep(subs_long[1:2],2)
  subslong2 = rep(subs_long[2:3],2)
  
  subslat1 = rep(subs_lat[1:2],each = 2)
  subslat2 = rep(subs_lat[2:3],each = 2)
  
  cond = cbind(subslong1,subslong2,subslat1,subslat2)
  
  i = 1:nrow(cond)
  cond <<- cond
  df <<- df
  locdf <<- locdf
  group_by_df <-function(i){
    longdf = locdf[,1]
    latdf = locdf[,2]
    
    id = which(longdf >=cond[i,1] &
                 longdf <=cond[i,2] &
                 latdf >= cond[i,3] &
                 latdf <= cond[i,4])
    return(id)
  }
  
  idli = lapply(i,group_by_df)
  idli <<- idli
  i <<- 1:length(idli)
  idre_fun <-function(i){
    len = length(idli[[i]])
    if(len == 0){
      return(i)
    }
  }
  idtell = lapply(i,idre_fun)
  idtell = do.call('c',idtell)
  
  idli = idli[-idtell]
  cond = cond[-idtell,]
  
  i = 1:length(idli)
  len_tell<-function(i){
    len = length(idli[[i]])
    return(len)
  }
  lens = lapply(i,len_tell)
  lens = do.call('c',lens)
  
  i <<- 1:length(idli)
  df <<- df
  cond <<- cond
  idli <<- idli
  
  long = seq(-180,180,0.5)
  lat = seq(-60,90,0.5)
  grid = expand.grid(long,lat)
  
  world = shp_management('world')
  
  inusa <-map.where(world,x=grid[,1],y=grid[,2])
  inusa <-!is.na(inusa)
  grid <-grid[inusa,]
  
  grid2 <<- grid
  
  cluster_krig <-function(i){
    
    library(gstat)
    library(raster)
    library(sp)
    idgrid = which(grid2[,1]>=cond[i,1]&
               grid2[,1]<=cond[i,2]&
               grid2[,2]>=cond[i,3]&
               grid2[,2]<=cond[i,4])
    
    tmpgrid = grid2[idgrid,]
    tmpgridloc = tmpgrid
    
    tmpgrid = SpatialPoints(tmpgrid)
    gridded(tmpgrid) = T
    
    m <- vgm(.59, "Sph", 874, .04)
    tmpdf = df[idli[[i]],]
    colnames(tmpdf) = c('long','lat','pr')
    coordinates(tmpdf) = ~long+lat
    
    tmpx = krige(pr~1,tmpdf,tmpgrid,
              model = m,beta = 5.9)
    
    pred = tmpx$var1.pred
    pred[which(pred<0)] = 0
    ret = data.frame(
      long = tmpgridloc[,1],
      lat = tmpgridloc[,2],
      pr = pred
    )
    return(ret)
  }
  
  cl = makeCluster(14)
  clusterExport(cl,c('i','cond','grid2',
                     'df','idli'))
  system.time(retdf <- parLapply(cl,i,cluster_krig))
  stopCluster(cl)
  
  retdf = do.call('rbind',retdf)
  
  xpred = x$var1.pred
  xpred[which(xpred<0)] =0
  retdf$pr - x$var1.pred
  
  coordinates(retdf) = ~long+lat
  gridded(retdf) = T
  r2 = raster(retdf)
  plot(r2)
}

































