}
trend_fun <- function(x){
  
  sub_fun_his <-function(x){
    x = ts(x,start = c(2003,1),
           frequency = 12)
    x = decompose(x)$trend
    
    xt = as.numeric(x)
    stand_fun <-function(y){
      y = (y - mean(y,na.rm = T))/(max(y,na.rm = T) -min(y,na.rm = T))
    }
    xy  = stand_fun(xt)
    return(xy)
  }
  sub_fun_fut <-function(x){
    x = ts(x,start = c(2017,7),
           frequency = 12)
    x = decompose(x)$trend
    
    xt = as.numeric(x)
    stand_fun <-function(y){
      y = (y - mean(y,na.rm = T))/(max(y,na.rm = T) -min(y,na.rm = T))
    }
    xy  = stand_fun(xt)
    return(xy)
  }
  
  
  if(is.null(ncol(x))){
    x = as.numeric(x)
    if(length(x)<=174){
      xm = sub_fun_his(x)
    }else{
      xm = sub_fun_fut(x)
    }
    
  }else{
    if(nrow(x)<=174){
      xm = apply(x,2,sub_fun_his)
    }else{
      xm = apply(x,2,sub_fun_fut)
    }
    
    
  }
  return(xm)
}
trend_funRaster <-function(x){
  x = as.numeric(x)
  xtell = mean(x)
  if(is.na(xtell)){
    x = NA
  }else{
    xt = ts(x,start = c(2003,1),
            frequency = 12)
    xt = decompose(xt)$trend
    xt = as.numeric(xt)
    naid = which(is.na(xt))
    xt = xt[-naid]
    xt = as.numeric(xt)
    xt = xt/(max(xt) - min(xt))
    source('/home/share/R_project/xinjiang_vapor/mmkTrend.R')
    
    mmk = mmkTrend(xt)$Zc
    mmk = as.numeric(mmk)
    x = mmk
  }
  return(x)
}
trend_index<-function(
  x,
  start=c(2003,1),
  fre = 12
){
  
  xts = ts(x,start = start,frequency = fre)
  xtrend = decompose(xts)$trend
  naid = which(is.na(xtrend))
  xtrend =xtrend[-naid]
  return(xtrend)
}
